import { Routes, Route, useNavigate} from "react-router-dom";
import { useEffect, useState } from "react";
import { GlobalContext } from './contexts/GlobalContext';
import * as recipesService from './services/recipesService';


import { Catalog } from "./components/Catalog/Catalog";
import { CreatePage } from "./components/CreatePage/CreatePage";
import { Details } from "./components/Details/Details";
import { EditPage } from "./components/EditPage/EditPage";
import { Header } from "./components/Header/Header";
import { Home } from "./components/Home/Home";
import { Login } from "./components/Login/Login";
import { Register } from "./components/Register/Register";
import { Footer } from "./components/Footer/Footer";

function App() {
      const [ recipes, setRecipes ] = useState([]);
      const [ editData, setEditDate ] = useState({});

      useEffect(() => {
        recipesService.getAll()
        .then(result => {
          setRecipes(result)
        })
      }, []);

  const navigate = useNavigate();

  const onCreateRecipe = async (data) => {
      // create newGame on server
      const newRecipe = await recipesService.postCreate(data);

      // update App state
      setRecipes(state => [...state, newRecipe])

      // redirect to catalog
      navigate('/catalog');

  }

  const onEditHandler = async (detailsId) => {
    // get id data from server
    const getEditData = await recipesService.getById(detailsId);

    // upDate state
    setEditDate(state => ({...state, ...getEditData}));

    navigate('/editPage');
  }

  const onEditRecipe = async (id, data) => {
      // edit data on server
      const editRecipe = await recipesService.editItem(id, data); 

      // upDate state
      setRecipes(state => state.map(r => r._id === id ? editRecipe : r));

      navigate(`/catalog/${id}`);
    }


  const onDeleteRecipe = async (detailsId) => {
      const choice = window.confirm('Are you sure you want to delete this!');

      if (choice) {
          // Delet from server
          const result = await recipesService.deleteItem(detailsId);

          // Delete from State
          setRecipes(state => state.filter(r => r._id !== detailsId));
          navigate('/catalog')
      }
  }


  const contextValues = {
      onCreateRecipe,
      onDeleteRecipe,
      onEditHandler,
      onEditRecipe,
      ...editData,
  }; 

  return (
      <GlobalContext.Provider value={contextValues}>
      <div id="box">
          <Header />

          <main id="main-content">
              <Routes>
                  <Route path='/' element={<Home />} /> 
                  <Route path='/logout' element={<Home />} /> 
                  <Route path='/login' element={<Login />} /> 
                  <Route path='/register' element={<Register />} /> 
                  <Route path='/createPage' element={<CreatePage />} /> 
                  <Route path='/editPage' element={<EditPage  />} /> 
                  <Route path='/catalog' element={<Catalog recipes={recipes}/>} /> 
                  <Route path='/catalog/:detailsId' element={ <Details />} /> 
              </Routes>
          </main>

          <Footer /> 
      </div>
      </GlobalContext.Provider>
  );
}

export default App;
