import { Routes, Route, useNavigate} from "react-router-dom";
import { useEffect, useState } from "react";


import * as gameService from "./services/gameService";

import { Catalog } from "./components/Catalog/Catalog";
import { CreatePage } from "./components/CreatePage/CreatePage";
import { Details } from "./components/Details/Details";
import { EditPage } from "./components/EditPage/EditPage";
import { Footer } from "./components/Footer/Footer";
import { Header } from "./components/Header/Header";
import { Home } from "./components/Home/Home";
import { Login } from "./components/Login/Login";
import { Register } from "./components/Register/Register";

function App() {
    const [ editUserData, setEditUserData ] = useState({});
    const [ games, setGames ] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        gameService.getAll()
        .then(result => {
            setGames(result)
        })
    }, []);

    const onCreateGameSubmit = async (data) => {

    // create newGame on server
     const newGame = await gameService.postCreate(data);

    // update App state
    setGames(state => [...state, newGame])

     // redirect to catalog
     navigate('/catalog');
    }

    const onEditGameSubmit = async (gameId, data) => {

        // save chamge Game info on server
         const newGame = await gameService.editItem(gameId, data);

        // update App state
        setGames(state => state.map(g => g._id === gameId ? newGame : g))
        console.log(games);
         // redirect to catalog
         navigate('/catalog');
        }

    const onEditHandler = async (gameId) => {
        const editData = await gameService.getById(gameId);
        setEditUserData(state => ({...state, ...editData}));
        navigate('/editPage');
    }

    const onDeleteGame = async (gameId) => {
		//delete from server
		const deleteResult = await gameService.deleteItem(gameId);


		//delete from state
		setGames(state => state.filter((g) => (g._id !== gameId)))
	}
    // const values = editUserData.category;
    // console.log(values);

  return (
    <div id="box">

        <Header />

        <main id="main-content">
            <Routes>
                <Route path='/' element={<Home />} /> 
                <Route path='/logout' element={<Home />} /> 
                <Route path='/login' element={<Login />} /> 
                <Route path='/register' element={<Register />} /> 
                <Route path='/createPage' element={<CreatePage onCreateGameSubmit={onCreateGameSubmit} />} /> 
                <Route path='/editPage' element={<EditPage {...editUserData} onEditGameSubmit={onEditGameSubmit}/>} /> 
                <Route path='/catalog' element={<Catalog games={games}/>} /> 
                <Route path='/catalog/:detailsId' element={
                    <Details 
                        games={games} 
                        onDeleteGame={onDeleteGame}
                        onEditHandler={onEditHandler}
                        // onEditGameSubmit={onEditGameSubmit}
                    />}
                /> 
            </Routes>
        </main>

    </div>
  );
}

export default App;
