import { Link, useNavigate } from "react-router-dom";
import { logout } from "../../services/recipesService";
import styles from './Header.module.css'

export const Header = () => {

    const navigate = useNavigate();

    async function onLogout() {
        await logout();
        navigate('/');
    }
    async function onLogin() {
        navigate('/login');
    }
    async function onRegister() {
        navigate('/register');
    }
    return(
    <header>
        <nav className={styles["navigation"]}>
            <h1 className={styles["logoTitle"]}>ChefSteps</h1>
            <ul className={styles["listNav"]} role="list">
                <li className={styles["item"]}><Link className="link" to="/">Home</Link></li>
                <li className={styles["item"]}><Link className="link" to="/about us">About us</Link></li>
                <li className={styles["item"]}><Link className="link" to="/catalog">Recipes</Link></li>
                <li className={styles["item"]}><Link className="link" to="/tools">Guides and Tools</Link></li>
                <li className={styles["item"]}><Link className="link" to="/classes">Classes</Link></li>
                <button className="btn" onClick={onLogin}>Login</button>
                <button className="btn" onClick={onRegister}>Register</button>
                <button className="btn" onClick={onLogout}>Logout</button>
            </ul>
        </nav>
    </header>
    );
}
