import { useContext, useState } from "react";
import { BasketContext } from "../../contexts/BasketContext";

import styles from './Catalog.module.css';
import { Link } from 'react-router-dom';
import { ShoppingBasket } from "../ShoppingBasket/ShoppingBasket";

export function Tools() {
  const [isClick, setIsClick] = useState(false);
  const [numberОfPurchases, setNumberОfPurchases] = useState(0);
  const { items, setItems, basketItems, setBasketItems } = useContext(BasketContext);

  function addItem(basketItem) {
    setBasketItems([...basketItems, basketItem]);
    setNumberОfPurchases(basketItems.length);
  }

  const click = () => {
    setIsClick(!isClick);
  };

  return (
    <section className={styles["recipes"]}>
      {isClick === true && <ShoppingBasket /> }
      <form id={styles["search"]}>
        <div className={styles["searchContainer"]}>
          <label htmlFor="leg-title">Tools&Gadgets</label>
          <Link to={`/createPage`} className="link">
            <button className="btn">CreateNewRecipe</button>
          </Link>
          <button className="btn" type="button" onClick={click} >
            <i className="fas fa-shopping-cart"></i>
          </button>
          <p>{numberОfPurchases}</p>
        </div>
      </form>
      <div className={styles["recipeContainer"]}>
        {items.map(i => (
          <div key={i._id} className={styles["card"]}>
            <div className={styles["card__imgContainer"]}>
              <img
                src={i.imageUrl}
                alt=""
                className={styles["card__imgContainer__img"]}
              />
            </div>
            <div className={styles["card__content"]}>
              <h2 className={styles["card__content__title"]}>{i.name}</h2>
              <p>Price: {i.price}</p>
              <button className="btn" onClick={() => addItem(i)}>
                AddToBasket
              </button>
            </div>
          </div>
        ))}
        {items.length === 0 && (
          <h3 className={styles["nostyles[-articles"]}>No articles yet</h3>
        )}
      </div>
    </section>
  );
}


