import { useState, useEffect, useReducer } from "react";
import { useParams, Link } from "react-router-dom";
import { useContext } from 'react'; 
import { GlobalContext } from '../../contexts/GlobalContext';

import * as appService from "../../services/appService";

import { AddComment } from './AddComment/AddComment';
import { reducer } from '../../reducer/reducer';

export const Details = ({
    onDeleteRecipe,
}) => {
    const { detailsId } = useParams();
    const { userId, isAuthenticated, userEmail } = useContext(GlobalContext);
    // const [ recipeData, setRecipeData ] = useState({});
    const [recipe, dispatch] = useReducer(reducer, {});


    // useEffect(() => {
    //     appService.getRecipeById(detailsId)
    //     .then(res => {
    //         setRecipeData(res);
    //     })
    // }, [detailsId])

    useEffect(() => {
        Promise.all([
            appService.getRecipeById(detailsId),
            appService.getCommentsById(detailsId),
        ]).then(([recipeData, comments]) => {
            // console.log(recipeData);
            // console.log(comments);
            const recipeState = {
                ...recipeData,
                comments,
            };
            // console.log(recipeState);

            dispatch({type: 'RECIPE_FETCH', payload: recipeState})
        });
    }, [detailsId]);

    const onCommentSubmit = async (values) => {
        const response = await appService.postCreateCommets(detailsId, values.comment);
        console.log(response);
        dispatch({
            type: 'COMMENT_ADD',
            payload: response,
            userEmail,
        });
    };

    const isOwner = recipe._ownerId === userId;
    console.log(recipe);
    return(
        <section className={styles["details"]}>

            <div className={styles["detailsContainer"]}>
                <div className={styles["detailImgContainer"]}>
                    <img src={recipe.imageUrl} alt="" className={styles["daetailImg"]} />
                </div>
                <div className={styles["detailContent"]}>
                    <h2 className={styles["recipeTitle"]}>Recipe Details</h2>
                    <h3 className={styles["recipeName"]}>{recipe.recipeName}</h3>
                    <span className={styles["recipeChef"]}>{recipe.chef}</span>
                    <p className={styles["recipeTimeForCooking"]}>{recipe.time}</p>
                    <p className={styles["recipeSummary"]}>{recipe.summary}</p>
                    {isOwner && (
                        <>
                        <button className={styles["btn"]} onClick={() => onDeleteRecipe(detailsId)}>Delete</button>
                        <Link to={`/catalog/${detailsId}/editPage`} className={styles["link"]} ><button className={styles["btn"]}>Edit</button></Link>
                        </>
                    )}
                    {isAuthenticated && (
                        <button className={styles["btn"]}>Rating</button>
                    )}
                </div>
                <div className={styles["details-comments"]}>
                    <h2>Comments:</h2>
                    <ul role="list">
                        {recipe.comments && recipe.comments.map(x => (
                    <>
                            <li key={x._id} className={styles["comment"]} >
                                <p>{x.author.email}: {x.comment}</p>
                            </li>
                    </>
                        ))}
                    </ul>

                    {!recipe.comments?.length && (
                        <p className={styles["no-comment"]}>No comments.</p>
                    )}
                </div>
                {isAuthenticated && <AddComment onCommentSubmit={onCommentSubmit} />}
            </div>


        </section>
    );
}





