import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useContext } from 'react'; 
import { GlobalContext } from './GlobalContext';

import { getById } from "../../services/gameService";
import { postCreate, getCommentsById } from '../../services/commentService'


export const Details = () => {
    const { games, onDeleteGame, onEditHandler } = useContext(GlobalContext);  
    const [username, setUsername] =useState('');
    const [comment, setComment] = useState('');
    const [comments, setComments] = useState([]);
    const {detailsId} = useParams();
    const [game, setGame] = useState({});

    useEffect(() => {
        getById(detailsId)
        .then(res => {
            setGame(res);
            return getCommentsById(detailsId)
        })
        .then(result => {
            setComments(result)
        })
    }, [detailsId]);

  const onCommentSubmit =async (e) => {
        e.preventDefault();

      await postCreate({
            detailsId,
            username,
            comment,
        })
        setUsername('');
        setComment('');
    }

  const onUsernameChange = (e) => {
      setUsername(e.target.value);
  }

  const onCommentChange = (e) => {
      setComment(e.target.value)
  }
    return(
        <section id="game-details">
            <h1>Game Details</h1>
            <div className="info-section">

                <div className="game-header">
                    <img className="game-img" src={games.imageUrl} />
                    <h1>{games.title}</h1>
                    <span className="levels">MaxLevel: {games.maxLevel}</span>
                    <p className="type">{games.category}</p>
                </div>

                <p className="text">
                    {games.summary}
                </p>




                {/* <!-- Bonus ( for Guests and Users ) --> */}
                <div className="details-comments">
                    <h2>Comments:</h2>
                    <ul>
                        {/* <!-- list all comments for current game (If any) --> */}
                        {comments.map(x => (
                            <li key={x._id} className="comment">
                            <p>{x.username}: {x.comment}</p>
                        </li>
                        ))}
                    </ul>

                    {/* <!-- Display paragraph: If there are no games in the database --> */}
                    {comments.length === 0 && (
                         <p className="no-comment">No comments.</p>
                    )}
                </div>




                {/* <!-- Edit/Delete buttons ( Only for creator of this game )  --> */}
                <div className="buttons">
                    <Link className="button" onClick={() => onEditHandler(detailsId)}>Edit</Link>
                    <Link to="/catalog"  className="button" onClick={() => onDeleteGame(detailsId)}>Delete</Link>
                </div>
            </div>

            {/* <!-- Bonus --> */}
            {/* <!-- Add Comment ( Only for logged-in users, which is not creators of the current game ) --> */}
            <article className="create-comment">
                <label>Add new comment:</label>
                <form className="form" onSubmit={onCommentSubmit}>
                    <input type="text" name="username" placeholder="Pesho" value={username} onChange={onUsernameChange}/>
                    <textarea name="comment" placeholder="Comment......" value={comment} onChange={onCommentChange}></textarea>
                    <input className="btn submit" type="submit" value="Add Comment" />
                </form>
            </article>

        </section>
    );
}
