import {
    get,
    post,
    put,
    patch,
    remove
} from "./requester";

const baseUrl = 'http://localhost:3030/jsonstore/games';

const getAll = async () => {
    const result = await get(baseUrl);
    const games = Object.values(result);

    return games;
}

const postCreate = async (gameData) => {
    const result = await post(baseUrl, gameData);

    console.log(result);
    return result;
}

const getById = async (detailsId) => {
    const result = await get(`${baseUrl}/${detailsId}`);
    console.log(result);
    return result;
}


export {
    getAll,
    postCreate,
    getById,
}
