import * as api from './api.js';

export const login = api.login;
export const register = api.register;
export const logout = api.logout;

const endpoints = {
    postComments: '/data/comments',
    getComments: (detaildId) => `/data/comments?where=gameId%3D%22${detaildId}%22`
}

export async function getCommentsById(detaildId) {
    return api.get(endpoints.getComments(detaildId));
}

export async function postCreate(data) {
    return api.post(endpoints.postComments, data)
}






