import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useContext } from 'react'; 
import { GlobalContext } from '../../contexts/GlobalContext';

import { recipesServiceFactory } from "../../services/recipesService";



export const Details = () => {
    const [ recipeData, setRecipeData ] = useState({});
    const { detailsId } = useParams();
    const { onDeleteRecipe, onEditHandler } = useContext(GlobalContext);


    useEffect(() => {
        recipesServiceFactory.getById(detailsId)
        .then(res => {
            setRecipeData(res);
        })
    }, [detailsId])

    return(
        <section className="details">

            <div className="detailsContainer">
                <div className="detailImgContainer">
                    <img src={recipeData.imageUrl} alt="" className="daetailImg" />
                </div>
                <div className="detailContent">
                    <h2 className="recipeTitle">Recipe Details</h2>
                    <h3 className="recipeName">{recipeData.recipeName}</h3>
                    <span className="recipeChef">{recipeData.chef}</span>
                    <p className="recipeTimeForCooking">{recipeData.time}</p>
                    <p className="recipeSummary">{recipeData.summary}</p>
                    <button className="btn" onClick={() => onDeleteRecipe(detailsId)}>Delete</button>
                    <button className="btn" onClick={() => onEditHandler(detailsId)}>Edit</button>
                    <button className="btn">Rating</button>
                </div>
            </div>

        </section>
    );
}





