import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useContext } from 'react'; 
import { GlobalContext } from '../../contexts/GlobalContext';

import * as recipesService from "../../services/recipesService";
import { postCreate, getCommentsById } from '../../services/commentService'



export const Details = () => {
    const [ recipeData, setRecipeData ] = useState({});
    const { detailsId } = useParams();

    useEffect(() => {
        recipesService.getById(detailsId)
        .then(res => {
            setRecipeData(res);
        })
    }, [detailsId])

    return(
        <section className="details">

            <div className="detailsContainer">
                <div className="detailImgContainer">
                    <img src="/image/Corned-Beef-05.jpg" alt="" className="daetailImg" />
                </div>
                <div className="detailContent">
                    <h2 className="recipeTitle">Recipe Details</h2>
                    <h3 className="recipeName">Pene Bologniese</h3>
                    <span className="recipeChef">Gordan Rammsy</span>
                    <p className="recipeTimeForCooking">Time for cooking: 45min</p>
