import { requestFactory } from './api';

const baseUrl = 'http://localhost:3030/data/recipes';

export const recipesServiceFactory = (token) => {
    const request = requestFactory(token);

    const getAll = async () => {
        const result = await request.get(baseUrl);
        const games = Object.values(result);
        console.log(games);
        return games;
    };

    const getById = async (recipeId) => {
        const result = await request.get(`${baseUrl}/${recipeId}`);

        return result;
    };

    const postCreate = async (recipeData) => {
        const result = await request.post(baseUrl, recipeData);

        console.log(result);

        return result;
    };

    const editItem = (recipeId, data) => request.put(`${baseUrl}/${recipeId}`, data);

    const deleteItem = (recipeId) => request.delete(`${baseUrl}/${recipeId}`);


    return {
        getAll,
        getById,
        postCreate,
        editItem,
        deleteItem,
    };
}







