import { useContext } from "react";
import { BasketContext } from "../../contexts/BasketContext";

import styles from './Catalog.module.css';
import { Link } from 'react-router-dom';



export function Tools() {
    const { items, setItems } = useContext(BasketContext);
    console.log(items);
    function addItem(item) {
        setItems([...items, item]);
      }

      return(
        <section className={styles["recipes"]}>

            <form id={styles["search"]} >
                <div className={styles["searchContainer"]}>
                    <label htmlFor="leg-title">Recipes:</label>
                    <Link to={`/createPage`}className="link"><button className="btn">CreateNewRecipe</button></Link>
                </div>
            </form>

            <div className={styles["recipeContainer"]}>

                {/* {items.map(t => <CatalogItem key={i._id} {...t}/>)} */}
                {items.map(i =>
                <div key={i._id + '1'} className={styles["card"]}>
                <div className={styles["card__imgContainer"]}>
                    <img src={i.imageUrl} alt="" className={styles["card__imgContainer__img"]} />
                </div>
                <div className={styles["card__content"]}>
                    <h2 className={styles["card__content__title"]}>{i.name}</h2>
                    <p>Price: {i.price}</p>
                    <button className="btn" onClick={() => addItem(i)}>AddToBasket</button>

                </div>
                </div>
                )}
                {items.length === 0 && (<h3 className={styles["nostyles[-articles"]}>No articles yet</h3>)}

            </div>
        </section>
    );

}


