import { useState } from "react"


export const EditPage = ({
    onEditHandler,
    onEditGameSubmit,
}) => {
    const gameData = onEditHandler();
    console.log(gameData);
    const [values, setValues] = useState({
        title: '',
        category: '',
        maxLevel: '',
        imageUrl: '',
        summary: '',
    })

    // setValues(gameData);

    const onChangeHandler = (e) => {
        setValues(state => ({...state, [e.target.name]: e.target.value}))
    }

    const onSubmit = (e) => {
        e.preventDefault();

        // onEditGameSubmit(gameData._id, values);
    }
    return(
        <section id="edit-page" className="auth">
            <form id="edit" onSubmit={onSubmit}>
                <div className="container">

                    <h1>Edit Game</h1>
                    <label htmlFor="leg-title">Legendary title:</label>
                    <input value={values.title} onChange={onChangeHandler} type="text" id="title" name="title" defaultValue="" />

                    <label htmlFor="category">Category:</label>
                    <input value={values.category} onChange={onChangeHandler} type="text" id="category" name="category" defaultValue="" />

                    <label htmlFor="levels">MaxLevel:</label>
                    <input value={values.maxLevel} onChange={onChangeHandler} type="number" id="maxLevel" name="maxLevel" min="1" defaultValue="" />

                    <label htmlFor="game-img">Image:</label>
                    <input value={values.imageUrl} onChange={onChangeHandler} type="text" id="imageUrl" name="imageUrl" defaultValue="" />

                    <label htmlFor="summary">Summary:</label>
                    <textarea value={values.summary} onChange={onChangeHandler} name="summary" id="summary"></textarea>
                    <input className="btn submit" type="submit" value="Edit Game" />

                </div>
            </form>
        </section>
    );
}



