import { Routes, Route, useNavigate} from "react-router-dom";
import { useEffect, useState } from "react";


import { deleteItem, getAll, postCreate } from "./services/gameService";

import { Catalog } from "./components/Catalog/Catalog";
import { CreatePage } from "./components/CreatePage/CreatePage";
import { Details } from "./components/Details/Details";
import { EditPage } from "./components/EditPage/EditPage";
import { Footer } from "./components/Footer/Footer";
import { Header } from "./components/Header/Header";
import { Home } from "./components/Home/Home";
import { Login } from "./components/Login/Login";
import { Register } from "./components/Register/Register";

function App() {
    const [ games, setGames ] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        getAll()
        .then(result => {
            setGames(result)
        })
    }, []);

    const onCreateGameSubmit = async (data) => {

    // create newGame on server
     const newGame = await postCreate(data);

    // update App state
    setGames(state => [...state, newGame])

     // redirect to catalog
     navigate('/catalog');
    }

    const onDeleteGame = async (gameId) => {
        console.log(gameId);
		//delete from server
		const deleteResult = await deleteItem(gameId);


		//delete from state
		setGames(state => state.filter((g) => (g._id !== gameId)))
	}

  return (
    <div id="box">

        <Header />

        <main id="main-content">
            <Routes>
                <Route path='/' element={<Home />} /> 
                <Route path='/logout' element={<Login />} /> 
                <Route path='/login' element={<Login />} /> 
                <Route path='/register' element={<Register />} /> 
                <Route path='/createPage' element={<CreatePage onCreateGameSubmit={onCreateGameSubmit} />} /> 
                <Route path='/editPage' element={<EditPage />} /> 
                <Route path='/catalog' element={<Catalog games={games}/>} /> 
                <Route path='/catalog/:detailsId' element={<Details games={games} onDeleteGame={onDeleteGame}/>} /> 
            </Routes>
        </main>

        <Footer />
    </div>
  );
}

export default App;
