import {
    get,
    post,
    put,
    patch,
    remove
} from "./requester";

const baseUrl = 'http://localhost:3030/jsonstore/comments';

const postCreate = async (data) => {
    const result = await post(baseUrl, data);

    console.log(result);

    return result;
}
const getCommentsById = async (detailsId) => {
    const query = encodeURIComponent(`detailsId="${detailsId}"`)
    const result = await get(`${baseUrl}?where=${query}`);
    const comments = Object.values(result);

    // console.log(comments);

    return comments;
}



export {
    postCreate,
    getCommentsById,
}

