import * as api from './api.js';



const endpoints = {
    all: '/data/recipes?sortBy=_createdOn%20desc',
    byId: '/data/recipes/',
    lastGames: '/data/recipes?sortBy=_createdOn%20desc&distinct=category',
    create: '/data/recipes',
    edit: '/data/recipes/',
    delete: '/data/recipes/',

}
export const factoryService = (token) => {
    const login = api.login;
    const register = api.register;
    const logout = api.logout;

    const getAll = async () => {
        const result = api.get(endpoints.all, token);
        return result;
    }

    const getById = async (id) => {
        const result = api.get(endpoints.byId + id, token);
        return result; 
    }
    const getLastGames = async () => {
        const result = api.get(endpoints.lastGames, token);
        return result; 
    }

    const postCreate = async (data) => {
        const result = api.post(endpoints.create, data)
        return result; 
    }

    const editItem = async (id, token, data) => {
        const result = api.put(endpoints.edit + id, data)
        return result; 
    } 

    const deleteItem = async (id) => {
        const result = api.del(endpoints.delete + id)
        return result; 
    }

    return(
        login,
        register,
        logout,
        getAll,
        getById,
        getLastGames,
        postCreate,
        editItem,
        deleteItem
    );
}




