import { register } from "../../services/gameService";

import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export const Register = () => {

      const navigate = useNavigate();

      const [ values, setValues ] = useState({
        email: '',
        password: '',
        'confirm-password': '',
      })  

      const onChangeSubmitHandler = (e) => {
          setValues(state => ({...state, [e.target.name]: e.target.value}))
      }

      const onRegisterHandler = async () => {
        if (values.email === '' || values.password === '') {
            alert('All fealds are required!');
            return;
        }
        if (values.password !== values[ 'confirm-password']) {
            alert('Password don\'t match');
            return;
        }

        await register(values.email, values.password);
      }

      const onSubmit = (e) => {
        e.preventDefault();

        onRegisterHandler();
        navigate('/catalog');
      }

    return(
        <section id="register-page" className="content auth">
            <form id="register" onSubmit={onSubmit}>
                <div className="container">
                    <div className="brand-logo"></div>
                    <h1>Register</h1>

                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" placeholder="maria@email.com" />

                    <label htmlFor="pass">Password:</label>
                    <input type="password" name="password" id="register-password" />

                    <label htmlFor="con-pass">Confirm Password:</label>
                    <input type="password" name="confirm-password" id="confirm-password" />

                    <input className="btn submit" type="submit" value="Register" />

                    <p className="field">
                        <span>If you already have profile click <Link to="/login">here</Link></span>
                    </p>
                </div>
            </form>
        </section>
    );
}


export function registerPage(ctx) {
    // console.log(ctx);
    //     ctx.render(registerTemp(createSubmitHandler(onRegister)));


    // async function onRegister({email, password, repeatPass}) {

    // if (email == '' || password == '') {
    //     alert('All fealds are required!');
    //     return;
    // }
    // if (password !== repeatPass) {
    //     alert('Password don\'t match');
    //     return;
    // }
 ctx.render(registerTemp(onRegister));
    async function onRegister(event) {
        event.preventDefault();

        const formData = new FormData(event.target);


        const email = formData.get('email');
        const password = formData.get('password');
        const rePassword = formData.get('confirm-password');

        if (email == '' || password == '') {
          alert('All fealds are required!');
          return;
      }
      if (password !== rePassword) {
          alert('Password don\'t match');
          return;
      }

      await register(email, password);
      // event.target.reset();
      ctx.updateUserNav();
      ctx.page.redirect('/');
    }
}
