import styles from './AboutUs.module.css';
import { Link } from 'react-router-dom';

export const AboutUs = ({
    recipes,
}) => {

    return(
            <section className={styles["recipes"]}>

                <div className={styles["recipeContainer"]}>

                    <div className={styles["card"]}>
                    <div className={styles["card__imgContainer"]}>
                        <img src={recipes.imageUrl} alt="" className={styles["card__imgContainer__img"]} />
                    </div>
                    <div className={styles["card__content"]}>
                        <h2 className={styles["card__content__title"]}>{recipes.recipeName}</h2>
                         <Link to={`/catalog/${recipes._id}`}className="link"><button className="btn">Details</button></Link>

                    </div>
                </div>

                    {recipes.length === 0 && (<h3 className={styles["nostyles[-articles"]}>No articles yet</h3>)}

                </div>
            </section>

    );
}
