import * as api from './api.js';

export const login = api.login;
export const register = api.register;
export const logout = api.logout;

const endpoints = {
    all: '/data/recipes?sortBy=_createdOn%20desc',
    byId: '/data/recipes/',
    lastGames: '/data/recipes?sortBy=_createdOn%20desc&distinct=category',
    create: '/data/recipes',
    edit: '/data/recipes/',
    delete: '/data/recipes/',

}

export async function getAll(token) {
    return api.get(endpoints.all, token);
}

export async function getById(id, token) {
    return api.get(endpoints.byId + id, token);
}
export async function getLastGames(token) {
    return api.get(endpoints.lastGames, token);
}

export async function postCreate(data, token) {
    return api.post(endpoints.create, token, data)
}

export async function editItem(id, token, data) {
    return api.put(endpoints.edit + id, token, data)
} 

export async function deleteItem(id, token) {
    return api.del(endpoints.delete + id, token)
}


