import * as appService from './appService';

// const baseUrl = 'http://localhost:3030/data/comments';

export const getAllRecipes = async (gameId) => {
    const searchQuery = encodeURIComponent(`gameId="${gameId}"`);
    const relationQuery = encodeURIComponent(`author=_ownerId:users`);

    const result = await appService.get(`$?where=${searchQuery}&load=${relationQuery}`);
    const comments = Object.values(result);

    return comments;
};

export const create = async (gameId, comment) => {
    const result = await appService.postComment({ gameId, comment });

    return result;
};



