const endpoints = {
    all: '/data/recipes?sortBy=_createdOn%20desc',
    byId: '/data/recipes/',
    lastGames: '/data/recipes?sortBy=_createdOn%20desc&distinct=category',
    create: '/data/recipes',
    edit: '/data/recipes/',
    delete: '/data/recipes/',

}



export async function getAll() {
    return api.get(endpoints.all);
}

export async function getById(id) {
    return api.get(endpoints.byId + id);
}
export async function getLastGames() {
    return api.get(endpoints.lastGames);
}

export async function postCreate(data) {
    return api.post(endpoints.create, data)
}

export async function editItem(id, data) {
    return api.put(endpoints.edit + id, data)
} 

export async function deleteItem(id) {
    return api.del(endpoints.delete + id)
}

export async function login(email, password) {
    const result = await post('/users/login', { email, password });
    const userData = {
    email: result.email,
    id: result._id,
    token: result.accessToken
    };
    setUserData(userData);
    return userData;
  }

  export async function register(email, password) {
    const result = await post('/users/register', {email, password});
    const userData = {
    email: result.email, 
    id: result._id, 
    token: result.accessToken,
    };
    setUserData(userData);
    return userData;
  }

  export async function logout() {
    await get('/users/logout'); 
    clearUserData();
  }


