import { useState } from 'react';
import { Link, useNavigate } from "react-router-dom"

import * as recipesService from '../../services/recipesService.js'

export const Login = () => {
    const [userData, setUserData] = useState({
        email: '',
        password: '',
    });
    const navigate = useNavigate();

    const onChangeHandler = (e) => {
        setUserData(state => ({...state, [e.target.name]: e.target.value}));
    }

    const onCreateAccount = async (data) => {

        if (data.email === '' || data.password === '') {
            alert('All fealds are required!');
            return;
        }
        console.log(data);
       return await recipesService.login(data.email, data.password);
    }

    const onSubmit = async (e) => {
        e.preventDefault();

        await onCreateAccount(userData);
        navigate('/login')
    }


    return(
    <section id="login-page" className="auth">
            <form id="login" onSubmit={onSubmit}>

                <div className="container">
                    <h1>Login</h1>
                    <label htmlFor="email">Email:</label>
                    <input value={userData.email} onChange={onChangeHandler} type="email" id="email" name="email" placeholder="alex@gmail.com" />

                    <label htmlFor="login-pass">Password:</label>
                    <input value={userData.password} onChange={onChangeHandler}  type="password" id="login-password" name="password" />
                    <input type="submit" className="btn submit" value="Login" />
                    <p className="field">
                        <span>If you don't have profile click <Link to="/register">here</Link></span>
                    </p>
                </div>
            </form>
        </section>
    );
}



