import { Routes, Route, useNavigate} from "react-router-dom";
import { useEffect, useState } from "react";

import { GlobalContext } from './contexts/GlobalContext';

import * as recipesService from "./services/recipesService";
import { getAll } from "./services/recipesService";

import { AboutUs } from "./components/AboutUs/AboutUs"
import { Catalog } from "./components/Catalog/Catalog";
import { CreatePage } from "./components/CreatePage/CreatePage";
import { Details } from "./components/Details/Details";
import { EditPage } from "./components/EditPage/EditPage";
import { Header } from "./components/Header/Header";
import { Home } from "./components/Home/Home";
import { Login } from "./components/Login/Login";
import { Register } from "./components/Register/Register";
import { Footer } from "./components/Footer/Footer";
import { Logout } from "./components/Logout/Logout";

function App() {
      const [ recipes, setRecipes ] = useState([]);
      const [ editData, setEditDate ] = useState({});
      const [ userState, setUserState ] = useState({});
      const navigate = useNavigate();

      useEffect(() => {
        getAll()
        .then(result => {
          setRecipes(result)
        })
      }, []);


  const onCreateRecipe = async (data) => {
        // create newGame on server
        const newRecipe = await recipesService.postCreate(data);

        // update App state
        setRecipes(state => [...state, newRecipe])

        // redirect to catalog
        navigate('/catalog');
    }

  const onEditHandler = async (detailsId) => {
        // get id data from server
        const getEditData = await recipesService.getById(detailsId);

        // upDate state
        setEditDate(state => ({...state, ...getEditData}));

        navigate('/editPage');
    }

  const onEditRecipe = async (id, data) => {
      // edit data on server
      const editRecipe = await recipesService.editItem(id, data); 

      // upDate state
      setRecipes(state => state.map(r => r._id === id ? editRecipe : r));

      navigate(`/catalog/${id}`);
    }


  const onDeleteRecipe = async (detailsId) => {
      const choice = window.confirm('Are you sure you want to delete this!');

      if (choice) {
          // Delet from server
          await recipesService.deleteItem(detailsId);

          // Delete from State
          setRecipes(state => state.filter(r => r._id !== detailsId));
          navigate('/catalog')
        }
    }

    const onLoginSubmit = async (data) => {
        if (data.email === '' || data.password === '') {
            alert('All fealds are required!');
            return;
        }
    try {
        const result = await recipesService.login(data.email, data.password);

        setUserState(result);

        navigate('/catalog');
    } catch (error) {
        alert(`error: ${error}`);
    }
    };

    // REGISTER
    const onRegisterSubmit = async (data) => {
        if (data.email === '' || data.password === '') {
            alert('All fealds are required!');
            return;
        } 

        if (data.password !== data['confirm-password']) {
            alert('Password don\'t match');
            return;
        }

            try {
                const result = await recipesService.register(data.email, data.password);

                setUserState(result);

                navigate('/catalog');
            } catch (error) {
                alert(`error: ${error}`);
            }

        };  

    // LOGOUT

    const onLogoutSubmit = async () => {
        await recipesService.logout();
        setUserState({});

    } 

    const contextValues = {
        onLoginSubmit,
        onRegisterSubmit,
        onLogoutSubmit,
        userId: userState._id,
        token: userState.accessToken,
        userEmail: userState.email,
        isAuthenticated: !!userState.accessToken,
    };

  return (
      <GlobalContext.Provider key={contextValues}>
      <div id="box">
          <Header />

          <main id="main-content">
              <Routes>
                  <Route path='/' element={<Home />} /> 
                  <Route path='/login' element={<Login onLoginSubmit={onLoginSubmit}/>} /> 
                  <Route path='/register' element={<Register />} /> 
                  <Route path='/logout' element={<Logout />} /> 
                  <Route path='/aboutUs' element={<AboutUs recipes={recipes} />} /> 
                  <Route path='/createPage' element={<CreatePage onCreateRecipe={onCreateRecipe}/>} /> 
                  <Route path='/editPage' element={<EditPage  onEditRecipe={onEditRecipe} {...editData}/>} /> 
                  <Route path='/catalog' element={<Catalog recipes={recipes}/>} /> 
                  <Route path='/catalog/:detailsId' element={ <Details onDeleteRecipe={onDeleteRecipe} onEditHandler={onEditHandler}/>} /> 
              </Routes>
          </main>

          <Footer /> 
      </div>
      </GlobalContext.Provider>
  );
}

export default App;
