import { useContext, useState } from "react";
import { BasketContext } from "../../contexts/BasketContext";

import styles from './Catalog.module.css';
import { Link } from 'react-router-dom';



export function Tools() {
      const [isClick, setIsClick] = useState(false);
      const { items, setItems, basketItems, setBasketItems } = useContext(BasketContext);
      function addItem(basketItem) {
         setBasketItems([...basketItems, basketItem]);
      }

      function removeItem(index) {
        const newItems = [...basketItems];
        newItems.splice(index, 1);
        setBasketItems(newItems);
      }

      function calculateTotal() {
        return basketItems.reduce((total, item) => total + item.price, 0);
      }

      const click = () => {
          setIsClick(!isClick)
      }

      return(
        <section className={styles["recipes"]}>
            {isClick && 
                <div>
            <h2>Shopping Cart</h2>
            {basketItems.length === 0 && <p>No items in cart</p>}
            {basketItems.map((item, index) => (
                <div key={index}>
                <p>{item.name} - ${item.price.toFixed(2)}</p>
                <button onClick={() => removeItem(index)}>Remove</button>
                </div>
            ))}
            {basketItems.length > 0 && (
                <div>
                <p>Total: ${calculateTotal().toFixed(2)}</p>
                <button>Checkout</button>
                </div>
            )}
            </div>
            }


            <form id={styles["search"]} >
                <div className={styles["searchContainer"]}>
                    <label htmlFor="leg-title">Tools&Gadgets</label>
                    <Link to={`/createPage`}className="link"><button className="btn">CreateNewRecipe</button></Link>
                    <button className="btn" onClick={click}><i class="fas fa-shopping-cart"></i></button>
                </div>
            </form>

            <div className={styles["recipeContainer"]}>

                {/* {items.map(t => <CatalogItem key={i._id} {...t}/>)} */}
                {items.map(i =>
                <div key={i._id} className={styles["card"]}>
                <div className={styles["card__imgContainer"]}>
                    <img src={i.imageUrl} alt="" className={styles["card__imgContainer__img"]} />
                </div>
                <div className={styles["card__content"]}>
                    <h2 className={styles["card__content__title"]}>{i.name}</h2>
                    <p>Price: {i.price}</p>
                    <button className="btn" onClick={() => addItem(i)}>AddToBasket</button>

                </div>
                </div>
                )}
                {items.length === 0 && (<h3 className={styles["nostyles[-articles"]}>No articles yet</h3>)}

            </div>
        </section>
    );

}


